function trimString(r){for(var n=0,t=r.length-1;n<r.length&&" "==r[n];)n++;for(;t>n&&" "==r[t];)t-=1;return r.substring(n,t+1)}function compareObjects(r,n){var t="";for(t in r)if(r[t]!=n[t])return!1;for(t in n)if(r[t]!=n[t])return!1;return!0}function itemExists(r,n){for(var t=0;t<r.length;t++)if(compareObjects(r[t],n))return!0;return!1}function searchFor(r,n){var t=[];n=trimString(n);for(var e=0;e<r.length;e++)for(var i in r[e])-1!=r[e][i].indexOf(n)&&(itemExists(t,r[e])||t.push(r[e]));return t}console.log(searchFor("lo "));